/**
 * Normaliza o nome de um exercício para criar uma chave de busca.
 * @param {string} name - O nome do exercício.
 * @returns {string} - O nome normalizado.
 */
export function normalizeName(name) {
    if (!name) return '';
    return name
        .toLowerCase()
        .normalize("NFD")
        .replace(/[\u0300-\u036f]/g, "")
        .replace(/[^a-z0-9]/g, '');
}
